import Navigationsleiste from"./Navigationsleiste.min.js";import Eingabeformular from"./Eingabeformular.min.js";import Monatslistensammlung from"./Monatslistensammlung.min.js";import Gesamtbilanz from"./Gesamtbilanz.min.js";import Eintrag from"./Eintrag.min.js";export default class Haushaltsbuch{constructor(){this._eintraege=[],this._navigationsleiste=new Navigationsleiste,this._eingabeformular=new Eingabeformular,this._monatslistensammlung=new Monatslistensammlung,this._gesamtbilanz=new Gesamtbilanz,this._wiederherstellen()}eintrag_hinzufuegen(e){let t=new Eintrag(e.titel,e.betrag,e.typ,e.datum);this._eintraege.push(t),this._monatslistensammlung._aktualisieren(this._eintraege),this._gesamtbilanz.aktualisieren(this._eintraege),this._speichern()}eintrag_entfernen(e){this._eintraege.forEach(t=>{t._timestamp==e&&this._eintraege.splice(this._eintraege.indexOf(t),1)}),this._monatslistensammlung._aktualisieren(this._eintraege),this._gesamtbilanz.aktualisieren(this._eintraege),this._speichern()}start(){this._navigationsleiste.anzeigen(),this._eingabeformular.anzeigen(),this._monatslistensammlung.anzeigen(),this._gesamtbilanz.anzeigen()}_speichern(){localStorage.setItem("eintraege",JSON.stringify(this._eintraege))}_wiederherstellen(){let e=localStorage.getItem("eintraege");null!==e&&JSON.parse(e).forEach(e=>{this.eintrag_hinzufuegen({titel:e._titel,betrag:e._betrag,typ:e._typ,datum:new Date(e._datum)})})}}